operation.add:
if ($this->config->get('smsassistent_status') == "1") {
    if ($this->config->get('smsassistent_customer_order_create_status') == "1") {
        if (isset($order_info['telephone'])) {
            $sms = new SMSAssistent($this->config);
            $sms->sendCustomerNotification($order_info['telephone']);
        }
    }
    if ($this->config->get('smsassistent_admin_order_create_status') == "1") {
        if (isset($order_info['telephone'])) {
            $sms = new SMSAssistent($this->config);
            $sms->sendAdminNotification();
        }
    }
}
